import React, {Component} from 'react';
import {Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, Button, CardMenu,CardText, IconButton} from "react-mdl";
import "../App.css"

class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = {activeTab: -1};
    }

    toggleCategories() {
        if (this.state.activeTab === 0) {
            return (
                <div>
                    <Card className="card-container">
                        <CardTitle className="card-title">HTML/CSS Project #1</CardTitle>

                        <CardText className="card-text">
                            The two languages—HTML and CSS—are independent of one another
                            and should remain that way. CSS should not be written inside of an HTML document
                            and vice versa....
                        </CardText>

                        <CardActions>
                            <Button colored>Github</Button>
                        </CardActions>
                        <CardMenu style={{color: '#fff'}}>
                            <IconButton name="share" />
                        </CardMenu>
                    </Card>
                </div>
            )
        } else if (this.state.activeTab === 1) {
            return (
                <div><h1>this is JavaScript</h1></div>
            )
        } else if (this.state.activeTab === 2) {
            return (
                <div><h1>this is React</h1></div>
            )
        } else if (this.state.activeTab === 3) {
            return (
                <div><h1>this is NodeJs</h1></div>
            )
        } else if (this.state.activeTab === 4) {
            return (
                <div><h1>this is Express/MongoDB</h1></div>
            )
        }

    }

    render() {
        return (
            <div className="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTab: tabId})} ripple>
                    <Tab>html/css</Tab>
                    <Tab>Javascript</Tab>
                    <Tab>React</Tab>
                    <Tab>NodeJs</Tab>
                    <Tab>express/MongoDB</Tab>
                </Tabs>

                <section className="projects-grid">
                    <Grid className="projects-grid">
                        <Cell col={12}>
                            <div className="project-content">{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>


                </section>

            </div>
        );
    }
}

export default Projects;
